import React from 'react'
import { connect } from 'react-redux'
import Router, { withRouter } from 'next/router'

import { authActions } from '../actions'

import { Row } from 'reactstrap'
import Layout from '../components/layout'

class Activate extends React.Component {

    componentDidMount() {
        this.props.dispatch(authActions.activateAccount(this.props.router.query.token))
    }

    componentDidUpdate() {
        if(!this.props.loading && this.props.list) {
            Router.push({
                pathname: '/login'
            })
        }
    }

    render() {
        return (
            <Layout>
                <Row className="page-content">
                    <div className="col-md-6 offset-md-3">
                        <div className="text-center">
                            {   this.props.loading &&
                                    <img alt="" src="data:image/gif;base64," />
                            }
                        </div>
                    </div>
                </Row>
            </Layout>
        )
    }

}


function mapStateToProps(state) {
    const { loading, list, error } = state.request
    return {
        list,
        loading,
        error,
    };
}

export default withRouter(connect(mapStateToProps)(Activate))
